import { useState, useEffect } from 'react'
import { Stack, Card, Form, Button, ButtonGroup, ToggleButton } from 'react-bootstrap'


export default function Menu(props) {

    const [given, setGiven] = useState("")
    const [want, setWant] = useState("")

    function hide(el) {
        el.style.display = 'none';
    }

    function show(el) {
        el.style.display = '';
    }

    const answerTypes = [
        {
            name: "Pinyin",
            value: "pinyin"
        },
        {
            name: "Character",
            value: "character",
        },
        {
            name: "Definition",
            value: "definition"
        }
    ]

    return (
        <Card body style={{ width: "400px" }}>
            <Card.Title>
                Mandarin Trainer
            </Card.Title>
            <Card.Body>
                <Stack gap={3}>

                    <Stack gap={1}>
                        <Form.Select aria-label="WordSet">
                            <option>Word Set</option>
                            <option value="1">Long Name of the Book 2-1 Chapter 3</option>
                            <option value="2">Two</option>
                            <option value="3">Three</option>
                        </Form.Select>
                        <Button>
                            Review Set
                        </Button>
                    </Stack>

                    <Stack direction="horizontal" gap={3} style={{ justifyContent: "space-between", alignItems: "center" }}>

                        <Stack>
                            <h6 style={{ textAlign: "center" }}>Given</h6>
                            <ButtonGroup vertical>
                                {answerTypes.map((radio, idx) => (
                                    <ToggleButton
                                        key={idx}
                                        id={`given-${idx}`}
                                        type="radio"
                                        name="given"
                                        value={radio.value}
                                        checked={given === radio.value}
                                        onChange={(e) => setGiven(e.currentTarget.value)}
                                    >
                                        {radio.name}
                                    </ToggleButton>
                                ))}
                            </ButtonGroup>
                        </Stack>

                        <Stack>
                            <h6 style={{ textAlign: "center" }}>Test for</h6>
                            <ButtonGroup vertical>
                                {answerTypes.map((radio, idx) => (
                                    <ToggleButton
                                        key={idx}
                                        id={`want-${idx}`}
                                        type="radio"
                                        name="want"
                                        value={radio.value}
                                        checked={want === radio.value}
                                        onChange={(e) => setWant(e.currentTarget.value)}
                                    >
                                        {radio.name}
                                    </ToggleButton>
                                ))}
                            </ButtonGroup>
                        </Stack>

                    </Stack>

                    <Button variant="success" onClick={() => {
                        hide(Menu);
                        //show(TestingZone);
                    }}>
                        GO!
                    </Button>

                </Stack>
            </Card.Body>
        </Card>
    )
}
